class Solution {
public:
    int countWays(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        int totalWays = 1; // At least one way (selecting none)
        int distinctWays = 1; // Counting the current distinct way
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            // If the current number is less than the number of elements to its right,
            // it means selecting i+1 students will make this and all previous students happy.
            if (nums[i] < i + 1) {
                distinctWays = 0;
            }
            distinctWays++;
            if (i + 1 == n || nums[i] != nums[i + 1]) {
                totalWays += distinctWays; // Add distinct ways for this segment
                distinctWays = 1; // Reset for next segment
            }
        }
        return totalWays;
    }
};
