class Solution {
public:
    int countWays(vector<int>& nums) {
        long long mod = 1e9 + 7;
        sort(nums.begin(), nums.end());
        long long total = 0;
        long long ways = 1;
        for (int i = 0; i < nums.size(); ++i) {
            if (i < nums[i]) continue; // Ensure we can select up to i students without violating happiness condition
            // Calculate number of ways to choose any number of students from the first (i+1) options
            ways = 1; // Reset ways for current i
            for (int j = 0; j <= i; ++j) {
                ways = (ways * 2) % mod; // Each student can either be selected or not, doubling the possibilities
            }
            total = (total + ways - 1) % mod; // Subtract 1 to exclude the case where no student is selected, as it's counted in all segments
        }
        return total;
    }
};
