public class Solution {
    public int[] LexicographicallySmallestArray(int[] nums, int limit) {
        for (int i = 0; i < nums.Length; i++) {
            int minIndex = i;
            for (int j = i + 1; j < nums.Length && j <= i + limit; j++) {
                if (nums[j] < nums[minIndex]) {
                    minIndex = j;
                }
            }
            if (minIndex != i) {
                for (int k = minIndex; k > i; k--) {
                    if (Math.Abs(nums[k] - nums[k - 1]) <= limit) {
                        int temp = nums[k];
                        nums[k] = nums[k - 1];
                        nums[k - 1] = temp;
                    } else {
                        break;
                    }
                }
            }
        }
        return nums;
    }
}
