class Solution {
    fun lexicographicallySmallestArray(nums: IntArray, limit: Int): IntArray {
        val n = nums.size
        val sortedIndices = nums.indices.sortedBy { nums[it] }
        val position = IntArray(n) { 0 }
        sortedIndices.forEachIndexed { index, value -> position[value] = index }

        for (i in 0 until n) {
            val minPosition = position[i]
            for (j in i until n) {
                if (Math.abs(nums[i] - nums[j]) <= limit && position[j] < minPosition) {
                    nums[i] = nums[j].also { nums[j] = nums[i] }
                    position[i] = position[j].also { position[j] = position[i] }
                    break
                }
            }
        }
        return nums
    }
}
