class Solution {
    fun lexicographicallySmallestArray(nums: IntArray, limit: Int): IntArray {
        val sortedNums = nums.sorted()
        val indexMap = mutableMapOf<Int, MutableList<Int>>()
        nums.forEachIndexed { index, value ->
            indexMap.getOrPut(value) { mutableListOf() }.add(index)
        }
        val visited = BooleanArray(nums.size)
        for (i in nums.indices) {
            if (!visited[i]) {
                val group = mutableListOf<Int>()
                val queue = ArrayDeque<Int>()
                queue.add(i)
                while (queue.isNotEmpty()) {
                    val current = queue.removeFirst()
                    if (visited[current]) continue
                    visited[current] = true
                    group.add(current)
                    indexMap[nums[current]]?.forEach { neighbor ->
                        if (!visited[neighbor] && Math.abs(current - neighbor) <= limit) {
                            queue.add(neighbor)
                        }
                    }
                }
                val sortedGroupValues = group.map { nums[it] }.sorted()
                group.sorted().forEachIndexed { index, valueIndex ->
                    nums[valueIndex] = sortedGroupValues[index]
                }
            }
        }
        return nums
    }
}
