class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $limit
     * @return Integer[]
     */
    function lexicographicallySmallestArray($nums, $limit) {
        $n = count($nums);
        $sorted = $nums;
        sort($sorted);
        $indexMap = [];
        foreach ($sorted as $i => $value) {
            $indexMap[$value][] = $i;
        }
        $used = array_fill(0, $n, false);
        for ($i = 0; $i < $n; ++$i) {
            if (!empty($indexMap[$nums[$i]])) {
                foreach ($indexMap[$nums[$i]] as $j => $sortedIndex) {
                    if (!$used[$sortedIndex]) {
                        $indexMap[$nums[$i]][$j] = -1; // Mark as used in map
                        $used[$sortedIndex] = true;
                        break;
                    }
                }
            }
            foreach ($sorted as $sortedValue) {
                if (abs($nums[$i] - $sortedValue) <= $limit) {
                    $sortedIndices = $indexMap[$sortedValue];
                    foreach ($sortedIndices as $sortedIndex) {
                        if ($sortedIndex >= 0 && !$used[$sortedIndex]) {
                            $nums[$i] = $sortedValue;
                            $used[$sortedIndex] = true;
                            $indexMap[$sortedValue][array_search($sortedIndex, $sortedIndices)] = -1; // Mark as used in map
                            break 2;
                        }
                    }
                } else {
                    break;
                }
            }
        }
        return $nums;
    }
}
