impl Solution {
    pub fn lexicographically_smallest_array(mut nums: Vec<i32>, limit: i32) -> Vec<i32> {
        let n = nums.len();
        let mut sorted = nums.clone();
        sorted.sort_unstable();
        let mut index_map = std::collections::HashMap::new();
        for i in 0..n {
            index_map.entry(nums[i]).or_insert_with(Vec::new).push(i);
        }

        'outer: for i in 0..n {
            if nums[i] == sorted[i] {
                continue;
            }
            for j in i + 1..n {
                if (nums[j] - nums[i]).abs() <= limit && nums[j] == sorted[i] {
                    for &k in index_map.get(&sorted[i]).unwrap() {
                        if k >= i && (nums[i] - nums[k]).abs() <= limit {
                            nums.swap(i, k);
                            continue 'outer;
                        }
                    }
                }
            }
        }

        nums
    }
}
