object Solution {
    def lexicographicallySmallestArray(nums: Array[Int], limit: Int): Array[Int] = {
        import scala.collection.mutable

        val sortedIndices = nums.indices.sortWith((i, j) => nums(i) < nums(j) || (nums(i) == nums(j) && i < j))
        val indexMap = mutable.Map[Int, Int]()
        sortedIndices.zipWithIndex.foreach { case (index, sortedIndex) => indexMap(index) = sortedIndex }

        def canSwap(i: Int, j: Int): Boolean = math.abs(nums(i) - nums(j)) <= limit

        for (i <- nums.indices) {
            var minPos = i
            for (j <- i + 1 until nums.length) {
                if (canSwap(i, j) && indexMap(j) < indexMap(minPos)) {
                    minPos = j
                }
            }
            if (minPos != i) {
                val temp = nums(i)
                nums(i) = nums(minPos)
                nums(minPos) = temp

                indexMap(minPos) = indexMap(i)
                indexMap(i) = minPos
            }
        }
        nums
    }
}
