class Solution {
    func lexicographicallySmallestArray(_ nums: [Int], _ limit: Int) -> [Int] {
        var nums = nums
        var sorted = nums.sorted()
        var indexMap = [Int: [Int]]()
        for i in 0..<nums.count {
            indexMap[nums[i], default: []].append(i)
        }

        for i in 0..<nums.count {
            if nums[i] != sorted[i] {
                for j in i+1..<nums.count {
                    if nums[j] == sorted[i], abs(nums[i] - nums[j]) <= limit {
                        nums.swapAt(i, j)
                        break
                    }
                }
            }
        }

        return nums
    }
}
