class Solution {
    func maximizeTheProfit(_ n: Int, _ offers: [[Int]]) -> Int {
        var dp = Array(repeating: 0, count: n + 1)
        var offersSorted = offers.sorted { $0[1] < $1[1] }
        var offerIndex = 0

        for i in 1...n {
            dp[i] = dp[i - 1] // Initialize with the previous maximum profit
            while offerIndex < offersSorted.count {
                let offer = offersSorted[offerIndex]
                if offer[1] < i {
                    offerIndex += 1
                    continue
                }
                if offer[1] == i - 1 {
                    dp[i] = max(dp[i], dp[offer[0]] + offer[2])
                }
                break
            }
        }

        return dp[n]
    }
}
