class Solution {
public:
    int maximumBeauty(vector<int>& nums, int k) {
        map<int, int> freq;
        for (int num : nums) {
            ++freq[num];
        }
        int max_beauty = 0, current_beauty = 0;
        auto left = freq.begin(), right = freq.begin();
        while (right != freq.end()) {
            if ((right->first - left->first) <= k) {
                current_beauty += right->second;
                max_beauty = max(max_beauty, current_beauty);
                ++right;
            } else {
                current_beauty -= left->second;
                ++left;
            }
        }
        return max_beauty;
    }
};
