class Solution {
public:
    int maximumBeauty(vector<int>& nums, int k) {
        sort(nums.begin(), nums.end());
        vector<int> prefixSum(nums.size() + 1, 0);
        for (int i = 1; i <= nums.size(); ++i) {
            prefixSum[i] = prefixSum[i - 1] + nums[i - 1];
        }

        int result = 1;
        for (int i = 0; i < nums.size(); ++i) {
            int low = i, high = nums.size() - 1, best = i;
            while (low <= high) {
                int mid = low + (high - low) / 2;
                int totalK = k * (mid - i + 1);
                int sumRange = prefixSum[mid + 1] - prefixSum[i];
                if (totalK + sumRange >= nums[mid] * (mid - i + 1)) {
                    best = mid;
                    low = mid + 1;
                } else {
                    high = mid - 1;
                }
            }
            result = max(result, best - i + 1);
        }
        return result;
    }
};
