int maximumBeauty(int* nums, int numsSize, int k) {
    int maxBeauty = 1;
    int* minMax = (int*)malloc(sizeof(int) * numsSize * 2); // Store min and max after applying operation
    for (int i = 0; i < numsSize; ++i) {
        minMax[i * 2] = nums[i] - k; // Min value
        minMax[i * 2 + 1] = nums[i] + k; // Max value
    }

    for (int i = 0; i < numsSize; ++i) {
        int currentMax = 1;
        for (int j = i + 1; j < numsSize; ++j) {
            if (!(minMax[j * 2] > minMax[i * 2 + 1] || minMax[j * 2 + 1] < minMax[i * 2])) {
                // If overlapping ranges
                currentMax++;
                maxBeauty = maxBeauty > currentMax ? maxBeauty : currentMax;
            }
        }
    }

    free(minMax);
    return maxBeauty;
}
