-spec maximum_beauty(Nums :: [integer()], K :: integer()) -> integer().
maximum_beauty(Nums, K) ->
    Sorted = lists:sort(Nums),
    {_, MaxBeauty} = lists:foldl(
        fun(X, {Start, Max}) ->
            End = X + 2*K,
            NewStart = case Sorted -- [Start] of
                            [] -> Start;
                            [H|_] when H =< End -> H;
                            _ -> Start
                        end,
            NewMax = max(Max, length([Y || Y <- Sorted, Y >= Start, Y =< End])),
            {NewStart, NewMax}
        end,
        {hd(Sorted), 0},
        Sorted
    ),
    MaxBeauty.
