func maximumBeauty(nums []int, k int) int {
    n := len(nums)
    if n == 1 {
        return 1
    }
    sort.Ints(nums)
    maxLen := 1
    for i := 0; i < n; i++ {
        left, right := i, n-1
        while left <= right {
            mid := left + (right-left)/2
            if nums[mid] <= nums[i]+k {
                maxLen = max(maxLen, mid-i+1)
                left = mid + 1
            } else {
                right = mid - 1
            }
        }
    }
    return maxLen
}

func max(a, b int) int {
    if a > b {
        return a
    }
    return b
}
