class Solution {
    public int maximumBeauty(int[] nums, int k) {
        Arrays.sort(nums);
        int left = 0, right = 0, max = 0, n = nums.length;
        int[] prefixSum = new int[n + 1];

        for (int i = 1; i <= n; ++i) {
            prefixSum[i] = prefixSum[i - 1] + nums[i - 1];
        }

        while (right < n) {
            while (left < right && !(nums[right] - nums[left] <= k)) {
                left++;
            }
            int cost = (nums[right] * (right - left + 1)) - (prefixSum[right + 1] - prefixSum[left]);
            if (cost <= k) {
                max = Math.max(max, right - left + 1);
            }
            right++;
        }

        return max;
    }
}
