class Solution(object):
    def maximumBeauty(self, nums, k):
        nums.sort()
        max_beauty, sum_diff, left = 1, 0, 0
        for right in range(1, len(nums)):
            sum_diff += (nums[right] - nums[right - 1]) * (right - left)
            while sum_diff > k:
                sum_diff -= nums[right] - nums[left]
                left += 1
            max_beauty = max(max_beauty, right - left + 1)
        return max_beauty

    def maximumBeauty(self, nums, k):
        nums.sort()
        freq = [0] * (2 * 10**5 + 1)  # Frequency array
        l, max_len = 0, 0
        for r in range(len(nums)):
            freq[nums[r]] += 1
            while nums[r] - nums[l] > k:
                freq[nums[l]] -= 1
                l += 1
            max_len = max(max_len, sum(freq[nums[l]:nums[r]+1]))
        return max_len
