impl Solution {
    pub fn maximum_beauty(nums: Vec<i32>, k: i32) -> i32 {
        let mut nums = nums;
        nums.sort_unstable();
        let (mut l, mut r, mut max_len, mut curr_sum) = (0, 0, 1, 0);
        while r < nums.len() {
            if nums[r] - nums[l] <= k {
                max_len = max_len.max((r - l + 1) as i32);
                r += 1;
            } else {
                curr_sum += nums[r] - nums[l] - k;
                while curr_sum > k {
                    curr_sum -= nums[r] - nums[l] - k;
                    l += 1;
                }
                r += 1;
            }
        }
        max_len
    }
}
