impl Solution {
    pub fn maximum_beauty(nums: Vec<i32>, k: i32) -> i32 {
        use std::collections::HashMap;

        let mut count_map = HashMap::new();
        for &num in &nums {
            *count_map.entry(num).or_insert(0) += 1;
        }

        let mut unique_nums: Vec<_> = count_map.keys().cloned().collect();
        unique_nums.sort_unstable();
        let mut max_beauty = 1;
        let mut left = 0;

        for right in 0..unique_nums.len() {
            while unique_nums[right] - unique_nums[left] > k {
                left += 1;
            }

            let mut beauty = 0;
            for i in left..=right {
                beauty += count_map[&unique_nums[i]];
            }

            max_beauty = max_beauty.max(beauty);
        }

        max_beauty
    }
}
