object Solution {
    def maximumBeauty(nums: Array[Int], k: Int): Int = {
        val freqMap = scala.collection.mutable.Map[Int, Int]()
        nums.foreach(n => freqMap(n) = freqMap.getOrElse(n, 0) + 1)
        val uniqueSortedNums = nums.distinct.sorted
        val prefixSum = Array.fill(uniqueSortedNums.length)(0)
        prefixSum(0) = freqMap(uniqueSortedNums(0))
        for (i <- 1 until uniqueSortedNums.length) {
            prefixSum(i) = prefixSum(i - 1) + freqMap(uniqueSortedNums(i))
        }
        var maxBeauty = 1
        for (i <- uniqueSortedNums.indices) {
            var l = i
            var r = uniqueSortedNums.length - 1
            var best = i
            while (l <= r) {
                val mid = l + (r - l) / 2
                if (uniqueSortedNums(mid) - uniqueSortedNums(i) <= k) {
                    best = mid
                    l = mid + 1
                } else {
                    r = mid - 1
                }
            }
            maxBeauty = maxBeauty.max(prefixSum(best) - prefixSum(i) + freqMap(uniqueSortedNums(i)))
        }
        maxBeauty
    }
}
