public class Solution {
    public int MaxNumberOfAlloys(int n, int k, int budget, IList<IList<int>> composition, IList<int> stock, IList<int> cost) {
        int maxAlloys = 0;
        for (int machine = 0; machine < k; machine++) {
            int totalCost = 0, alloys = 0;
            while (true) {
                int alloyCost = 0;
                for (int metal = 0; metal < n; metal++) {
                    int required = composition[machine][metal] * (alloys + 1) - stock[metal];
                    if (required > 0) {
                        alloyCost += required * cost[metal];
                    }
                }
                if (totalCost + alloyCost > budget) break;
                totalCost += alloyCost;
                alloys++;
            }
            maxAlloys = Math.Max(maxAlloys, alloys);
        }
        return maxAlloys;
    }
}
