#include <vector>
using namespace std;

class Solution {
public:
    int maxNumberOfAlloys(int n, int k, int budget, vector<vector<int>>& composition, vector<int>& stock, vector<int>& cost) {
        int maxAlloys = 0;
        for (int i = 0; i < k; ++i) {
            int minAlloyPossible = INT_MAX;
            for (int j = 0; j < n; ++j) {
                int totalNeeded = composition[i][j];
                if (totalNeeded > 0) {
                    int canProduce = stock[j] / totalNeeded;
                    int additionalRequired = totalNeeded - stock[j] % totalNeeded;
                    int totalCost = additionalRequired * cost[j];
                    int additionalCanProduce = budget / totalCost;
                    minAlloyPossible = min(minAlloyPossible, canProduce + additionalCanProduce);
                }
            }
            maxAlloys = max(maxAlloys, minAlloyPossible);
        }
        return maxAlloys;
    }
};
