#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int maxNumberOfAlloys(int n, int k, int budget, vector<vector<int>>& composition, vector<int>& stock, vector<int>& cost) {
        int result = 0;
        for (int i = 0; i < k; ++i) {
            int minAlloys = INT_MAX;
            for (int j = 0; j < n; ++j) {
                int needed = composition[i][j];
                int available = stock[j];
                int totalCost = 0;
                if (needed > 0) {
                    if (needed * cost[j] > budget) {
                        minAlloys = 0;
                        break;
                    }
                    if (available < needed) {
                        totalCost = (needed - available) * cost[j];
                    }
                    int possibleAlloys = (budget - totalCost) / (needed * cost[j]);
                    minAlloys = min(minAlloys, possibleAlloys + (available / needed));
                }
            }
            result = max(result, minAlloys);
        }
        return result;
    }
};
