class Solution {
  int maxNumberOfAlloys(int n, int k, int budget, List<List<int>> composition, List<int> stock, List<int> cost) {
    int result = 0;
    for (int i = 0; i < k; i++) {
      int minPossible = 1;
      int maxPossible = 1000000000;
      while (minPossible <= maxPossible) {
        int mid = minPossible + (maxPossible - minPossible) ~/ 2;
        int requiredBudget = 0;
        for (int j = 0; j < n; j++) {
          int required = composition[i][j] * mid;
          int needToBuy = required > stock[j] ? required - stock[j] : 0;
          requiredBudget += needToBuy * cost[j];
        }
        if (requiredBudget <= budget) {
          result = mid > result ? mid : result;
          minPossible = mid + 1;
        } else {
          maxPossible = mid - 1;
        }
      }
    }
    return result;
  }
}
