func maxNumberOfAlloys(n int, k int, budget int, composition [][]int, stock []int, cost []int) int {
 maxAlloys := 0
 for i := 0; i < k; i++ {
  totalCost := 0
  maxPossible := int(1e9)
  for j := 0; j < n; j++ {
   required := composition[i][j]
   if required > 0 {
    available := stock[j]
    if required <= available {
     continue
    }
    needed := required - available
    totalCostForMetal := needed * cost[j]
    totalCost += totalCostForMetal
    maxForThisMetal := (budget - totalCost) / cost[j]
    if maxForThisMetal < maxPossible {
     maxPossible = maxForThisMetal
    }
   }
  }
  if totalCost <= budget {
   alloysWithCurrentMachine := 1
   for totalCost <= budget && alloysWithCurrentMachine <= maxPossible {
    totalCost += sumOfCosts(composition[i], cost, stock)
    if totalCost <= budget {
     alloysWithCurrentMachine++
    }
   }
   if alloysWithCurrentMachine-1 > maxAlloys {
    maxAlloys = alloysWithCurrentMachine - 1
   }
  }
 }
 return maxAlloys
}

func sumOfCosts(composition []int, cost []int, stock []int) int {
 total := 0
 for i, required := range composition {
  if required > stock[i] {
   needed := required - stock[i]
   total += needed * cost[i]
  }
 }
 return total
}
