class Solution {
    public int maxNumberOfAlloys(int n, int k, int budget, List<List<Integer>> composition, List<Integer> stock, List<Integer> cost) {
        int maxAlloys = 0;
        for (int i = 0; i < k; i++) {
            int totalCost = 0;
            for (int j = 0; j < n; j++) {
                int requiredMetal = composition.get(i).get(j);
                int availableStock = stock.get(j);
                int metalCost = cost.get(j);
                if (requiredMetal > availableStock) {
                    totalCost += (requiredMetal - availableStock) * metalCost;
                }
            }
            if (totalCost <= budget) {
                int minAlloys = Integer.MAX_VALUE;
                for (int j = 0; j < n; j++) {
                    int requiredMetalPerAlloy = composition.get(i).get(j);
                    if (requiredMetalPerAlloy > 0) {
                        int totalMetalAvailable = stock.get(j) + (budget - totalCost) / cost.get(j);
                        minAlloys = Math.min(minAlloys, totalMetalAvailable / requiredMetalPerAlloy);
                    }
                }
                maxAlloys = Math.max(maxAlloys, minAlloys);
            }
        }
        return maxAlloys;
    }
}
