class Solution {
    public int maxNumberOfAlloys(int n, int k, int budget, List<List<Integer>> composition, List<Integer> stock, List<Integer> cost) {
        int maxAlloys = 0;
        for (int machine = 0; machine < k; machine++) {
            int minPossibleAlloys = Integer.MAX_VALUE;
            for (int metal = 0; metal < n; metal++) {
                int totalNeeded = composition.get(machine).get(metal);
                int totalCostForMetal = (totalNeeded > stock.get(metal)) ? (totalNeeded - stock.get(metal)) * cost.get(metal) : 0;
                if (totalNeeded == 0) continue; // Avoid division by zero
                int possibleAlloysForMetal = budget / totalCostForMetal;
                minPossibleAlloys = Math.min(minPossibleAlloys, possibleAlloysForMetal);
            }
            int totalCost = 0;
            for (int metal = 0; metal < n; metal++) {
                int neededForOneAlloy = composition.get(machine).get(metal);
                int extraNeeded = Math.max(0, neededForOneAlloy * minPossibleAlloys - stock.get(metal));
                totalCost += extraNeeded * cost.get(metal);
            }
            if (totalCost <= budget) {
                for (int alloys = minPossibleAlloys; alloys >= 1; alloys--) {
                    totalCost = 0;
                    for (int metal = 0; metal < n; metal++) {
                        int neededForAlloys = composition.get(machine).get(metal) * alloys;
                        int extraNeeded = Math.max(0, neededForAlloys - stock.get(metal));
                        totalCost += extraNeeded * cost.get(metal);
                    }
                    if (totalCost <= budget) {
                        maxAlloys = Math.max(maxAlloys, alloys);
                        break;
                    }
                }
            }
        }
        return maxAlloys;
    }
}
