/**
 * @param {number} n
 * @param {number} k
 * @param {number} budget
 * @param {number[][]} composition
 * @param {number[]} stock
 * @param {number[]} cost
 * @return {number}
 */
var maxNumberOfAlloys = function(n, k, budget, composition, stock, cost) {
    let maxAlloys = 0;
    for (let i = 0; i < k; i++) { // Iterate through each machine
        let totalCost = 0;
        for (let j = 0; j < n; j++) { // Iterate through each metal
            // Calculate the cost for the required amount of metal j for machine i
            let required = composition[i][j];
            let available = stock[j];
            let additionalCost = (required > available) ? (required - available) * cost[j] : 0;
            totalCost += additionalCost;
        }
        // Determine the maximum number of alloys that can be made by machine i within the budget
        let alloys = Math.floor(budget / totalCost);
        maxAlloys = Math.max(maxAlloys, alloys);
    }
    return maxAlloys;
};
