/**
 * @param {number} n
 * @param {number} k
 * @param {number} budget
 * @param {number[][]} composition
 * @param {number[]} stock
 * @param {number[]} cost
 * @return {number}
 */
var maxNumberOfAlloys = function(n, k, budget, composition, stock, cost) {
    let maxAlloys = 0;
    for (let machine = 0; machine < k; machine++) {
        let low = 0, high = budget, maxMachineAlloys = 0;
        while (low <= high) {
            let mid = Math.floor((low + high) / 2);
            let costForMidAlloys = 0;
            for (let metal = 0; metal < n; metal++) {
                let totalNeeded = composition[machine][metal] * mid;
                let needToBuy = Math.max(0, totalNeeded - stock[metal]);
                costForMidAlloys += needToBuy * cost[metal];
            }
            if (costForMidAlloys <= budget) {
                maxMachineAlloys = mid;
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        maxAlloys = Math.max(maxAlloys, maxMachineAlloys);
    }
    return maxAlloys;
};
