class Solution {
    fun maxNumberOfAlloys(n: Int, k: Int, budget: Int, composition: List<List<Int>>, stock: List<Int>, cost: List<Int>): Int {
        var maxAlloys = 0
        for (machine in 0 until k) {
            var totalCost = 0
            var possible = true
            for (metal in 0 until n) {
                val required = composition[machine][metal]
                val available = stock[metal]
                val additional = required - available
                if (additional > 0) {
                    totalCost += additional * cost[metal]
                }
                if (totalCost > budget) {
                    possible = false
                    break
                }
            }
            if (possible) {
                var low = 0
                var high = budget
                while (low <= high) {
                    val mid = (low + high) / 2
                    var currentCost = 0
                    for (metal in 0 until n) {
                        val needed = mid * composition[machine][metal] - stock[metal]
                        if (needed > 0) {
                            currentCost += needed * cost[metal]
                        }
                    }
                    if (currentCost <= budget) {
                        maxAlloys = maxOf(maxAlloys, mid)
                        low = mid + 1
                    } else {
                        high = mid - 1
                    }
                }
            }
        }
        return maxAlloys
    }
}
