class Solution {
    fun maxNumberOfAlloys(n: Int, k: Int, budget: Int, composition: List<List<Int>>, stock: List<Int>, cost: List<Int>): Int {
        var maxAlloys = 0
        for (machine in 0 until k) {
            var minAlloysPossible = Int.MAX_VALUE
            for (metal in 0 until n) {
                val totalMetalNeeded = composition[machine][metal]
                val totalCostForMetal = if (totalMetalNeeded > 0) (budget + stock[metal] * cost[metal]) / (totalMetalNeeded * cost[metal]) else Int.MAX_VALUE
                minAlloysPossible = minOf(minAlloysPossible, totalCostForMetal)
            }
            maxAlloys = maxOf(maxAlloys, minAlloysPossible)
        }
        return maxAlloys
    }
}
