class Solution {

    /**
     * @param Integer $n
     * @param Integer $k
     * @param Integer $budget
     * @param Integer[][] $composition
     * @param Integer[] $stock
     * @param Integer[] $cost
     * @return Integer
     */
    function maxNumberOfAlloys($n, $k, $budget, $composition, $stock, $cost) {
        $maxAlloys = 0;
        for ($machine = 0; $machine < $k; $machine++) {
            $minAlloysPossible = PHP_INT_MAX;
            for ($metalType = 0; $metalType < $n; $metalType++) {
                $totalMetalNeeded = $composition[$machine][$metalType] - $stock[$metalType];
                if ($totalMetalNeeded > 0) {
                    $alloysPossibleWithThisMetal = floor($budget / ($totalMetalNeeded * $cost[$metalType]));
                } else {
                    $alloysPossibleWithThisMetal = PHP_INT_MAX;
                }
                $minAlloysPossible = min($minAlloysPossible, $alloysPossibleWithThisMetal);
            }
            $maxAlloys = max($maxAlloys, $minAlloysPossible);
        }
        return $maxAlloys;
    }
}
