class Solution {

    /**
     * @param Integer $n
     * @param Integer $k
     * @param Integer $budget
     * @param Integer[][] $composition
     * @param Integer[] $stock
     * @param Integer[] $cost
     * @return Integer
     */
    function maxNumberOfAlloys($n, $k, $budget, $composition, $stock, $cost) {
        $maxAlloys = 0;
        for ($i = 0; $i < $k; $i++) {
            $low = 0;
            $high = $budget;
            while ($low <= $high) {
                $mid = intdiv($low + $high, 2);
                $totalCost = 0;
                for ($j = 0; $j < $n; $j++) {
                    $metalNeeded = $mid * $composition[$i][$j];
                    $buyNeeded = max(0, $metalNeeded - $stock[$j]);
                    $totalCost += $buyNeeded * $cost[$j];
                }
                if ($totalCost <= $budget) {
                    $maxAlloys = max($maxAlloys, $mid);
                    $low = $mid + 1;
                } else {
                    $high = $mid - 1;
                }
            }
        }
        return $maxAlloys;
    }
}
