class Solution(object):
    def maxNumberOfAlloys(self, n, k, budget, composition, stock, cost):
        max_alloys = 0
        for machine in range(k):
            total_cost = 0
            for metal in range(n):
                required_units = composition[machine][metal]
                if stock[metal] < required_units:
                    total_cost += (required_units - stock[metal]) * cost[metal]
                if total_cost > budget:
                    break
            else:
                # Calculate maximum number of alloys for this machine
                min_alloys = budget // total_cost if total_cost else float('inf')
                for metal in range(n):
                    if composition[machine][metal] > 0:
                        available_units = stock[metal] + (budget - total_cost) // cost[metal]
                        min_alloys = min(min_alloys, available_units // composition[machine][metal])
                max_alloys = max(max_alloys, min_alloys)
        return max_alloys
