from typing import List

class Solution:
    def maxNumberOfAlloys(self, n: int, k: int, budget: int, composition: List[List[int]], stock: List[int], cost: List[int]) -> int:
        max_alloys = 0
        for machine in range(k):
            max_machine_alloys = float('inf')
            for metal in range(n):
                metal_needed = max(0, composition[machine][metal] - stock[metal])
                if metal_needed > 0:
                    max_alloys_with_budget = (budget // (metal_needed * cost[metal]))
                else:
                    total_metal_available = stock[metal] // composition[machine][metal]
                    max_alloys_with_budget = total_metal_available if total_metal_available > 0 else float('inf')
                max_machine_alloys = min(max_machine_alloys, max_alloys_with_budget)

            # Calculate the total cost for creating max_machine_alloys with this machine
            total_cost = sum(max(0, composition[machine][metal] * max_machine_alloys - stock[metal]) * cost[metal] for metal in range(n))
            if total_cost <= budget:
                max_alloys = max(max_alloys, max_machine_alloys)
            else:
                # Adjust the number of alloys to fit the budget
                while max_machine_alloys > 0:
                    max_machine_alloys -= 1
                    total_cost = sum(max(0, composition[machine][metal] * max_machine_alloys - stock[metal]) * cost[metal] for metal in range(n))
                    if total_cost <= budget:
                        max_alloys = max(max_alloys, max_machine_alloys)
                        break
        return max_alloys
