impl Solution {
    pub fn max_number_of_alloys(n: i32, k: i32, budget: i32, composition: Vec<Vec<i32>>, stock: Vec<i32>, cost: Vec<i32>) -> i32 {
        let mut max_alloys = 0;
        for machine in 0..k as usize {
            let mut left = 0;
            let mut right = 100000; // Set the right boundary high enough to cover the possible maximum alloys
            while left < right {
                let mid = (left + right + 1) / 2; // Aim to find the maximum possible number of alloys
                let mut sum_cost = 0;
                for j in 0..n as usize {
                    let required = composition[machine][j] * mid; // Total required units of each metal
                    let shortage = required.saturating_sub(stock[j]); // Calculate shortage, if any
                    sum_cost += shortage * cost[j]; // Total cost to cover the shortage
                }
                if sum_cost <= budget {
                    left = mid; // If within budget, try a higher number
                } else {
                    right = mid - 1; // Otherwise, lower the search range
                }
            }
            max_alloys = max_alloys.max(left); // Keep the maximum number of alloys that can be produced
        }
        max_alloys
    }
}
