object Solution {
    def maxNumberOfAlloys(n: Int, k: Int, budget: Int, composition: List[List[Int]], stock: List[Int], cost: List[Int]): Int = {
        var maxAlloys = 0
        for (machine <- composition.indices) {
            var lo = 0
            var hi = budget
            while (lo <= hi) {
                val mid = lo + (hi - lo) / 2
                var tempBudget = budget
                for (metal <- 0 until n) {
                    val required = composition(machine)(metal) * mid
                    val buy = Math.max(0, required - stock(metal))
                    tempBudget -= buy * cost(metal)
                }
                if (tempBudget >= 0) {
                    maxAlloys = Math.max(maxAlloys, mid)
                    lo = mid + 1
                } else {
                    hi = mid - 1
                }
            }
        }
        maxAlloys
    }
}
