class Solution {
    func maxNumberOfAlloys(_ n: Int, _ k: Int, _ budget: Int, _ composition: [[Int]], _ stock: [Int], _ cost: [Int]) -> Int {
        var maxAlloys = 0
        for machine in 0..<k {
            var totalCost = 0
            var minAlloys = Int.max
            for metal in 0..<n {
                let requiredMetal = composition[machine][metal]
                let availableMetal = stock[metal]
                let metalCost = cost[metal]
                if requiredMetal > 0 {
                    let additionalMetalNeeded = max(0, requiredMetal - availableMetal)
                    totalCost += additionalMetalNeeded * metalCost
                    minAlloys = min(minAlloys, (budget - totalCost) / (requiredMetal * metalCost) + availableMetal / requiredMetal)
                }
            }
            if totalCost <= budget {
                for metal in 0..<n {
                    let requiredMetal = composition[machine][metal]
                    if requiredMetal > 0 {
                        let possibleAlloys = (stock[metal] + (budget - totalCost) / cost[metal]) / requiredMetal
                        minAlloys = min(minAlloys, possibleAlloys)
                    }
                }
            }
            maxAlloys = max(maxAlloys, minAlloys)
        }
        return maxAlloys
    }
}
