class Solution {
    func maxNumberOfAlloys(_ n: Int, _ k: Int, _ budget: Int, _ composition: [[Int]], _ stock: [Int], _ cost: [Int]) -> Int {
        var maxAlloys = 0
        for machineIndex in 0..<k {
            var low = 0
            var high = budget
            while low <= high {
                let mid = low + (high - low) / 2
                var currentCost = 0
                for metalIndex in 0..<n {
                    let requiredMetal = composition[machineIndex][metalIndex] * mid
                    let metalToBuy = max(0, requiredMetal - stock[metalIndex])
                    currentCost += metalToBuy * cost[metalIndex]
                }
                if currentCost <= budget {
                    maxAlloys = max(maxAlloys, mid)
                    low = mid + 1
                } else {
                    high = mid - 1
                }
            }
        }
        return maxAlloys
    }
}
