function maxNumberOfAlloys(n: number, k: number, budget: number, composition: number[][], stock: number[], cost: number[]): number {
    let maxAlloys = 0;
    for (let i = 0; i < k; i++) { // Loop through each machine
        let minPossibleAlloys = Number.MAX_SAFE_INTEGER;
        for (let j = 0; j < n; j++) { // Loop through each metal type
            let needed = composition[i][j] * (stock[j] + 1) - stock[j]; // Calculate how much of each metal type is needed
            let canProduce = Math.floor((budget + stock[j] * cost[j]) / (composition[i][j] * cost[j])); // Calculate how many alloys can be produced with current metal
            minPossibleAlloys = Math.min(minPossibleAlloys, canProduce, Math.max(0, Math.floor((budget - needed * cost[j] + stock[j] * cost[j]) / (composition[i][j] * cost[j])))); // Update the minimum possible alloys that can be produced
        }
        maxAlloys = Math.max(maxAlloys, minPossibleAlloys); // Update the maximum number of alloys that can be produced
    }
    return maxAlloys;
}
