function maxNumberOfAlloys(n: number, k: number, budget: number, composition: number[][], stock: number[], cost: number[]): number {
    let maxAlloys = 0;
    for (let machine = 0; machine < k; machine++) {
        let totalCost = 0;
        for (let metalType = 0; metalType < n; metalType++) {
            let metalNeeded = composition[machine][metalType] - stock[metalType];
            if (metalNeeded < 0) metalNeeded = 0;
            totalCost += metalNeeded * cost[metalType];
        }
        if (totalCost <= budget) {
            let low = 1, high = budget;
            while (low <= high) {
                let mid = Math.floor((low + high) / 2);
                let currentCost = 0;
                for (let metalType = 0; metalType < n; metalType++) {
                    let metalNeeded = composition[machine][metalType] * mid - stock[metalType];
                    if (metalNeeded < 0) metalNeeded = 0;
                    currentCost += metalNeeded * cost[metalType];
                }
                if (currentCost <= budget) {
                    maxAlloys = Math.max(maxAlloys, mid);
                    low = mid + 1;
                } else {
                    high = mid - 1;
                }
            }
        }
    }
    return maxAlloys;
}
