function maxNumberOfAlloys(n: number, k: number, budget: number, composition: number[][], stock: number[], cost: number[]): number {
    let maxAlloys = 0;
    for (let i = 0; i < k; i++) {
        let totalCost = 0;
        for (let j = 0; j < n; j++) {
            if (stock[j] < composition[i][j]) {
                totalCost += (composition[i][j] - stock[j]) * cost[j];
            }
        }
        if (totalCost > budget) continue;
        let low = 1, high = budget, possibleAlloys = 0;
        while (low <= high) {
            let mid = Math.floor((low + high) / 2);
            let currentCost = 0;
            for (let j = 0; j < n; j++) {
                let additionalMetalsNeeded = Math.max(0, composition[i][j] * mid - stock[j]);
                currentCost += additionalMetalsNeeded * cost[j];
            }
            if (currentCost <= budget) {
                possibleAlloys = mid;
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        maxAlloys = Math.max(maxAlloys, possibleAlloys);
    }
    return maxAlloys;
}
