public class Solution {
    public long FindMaximumNumber(long k, int x) {
        long low = 1, high = k;
        while (low < high) {
            long mid = (low + high + 1) / 2;
            long sum = 0;
            for (long i = 1; i <= x; i++) {
                sum += (mid >> i) + ((mid & ((1L << i) - 1)) >= (1L << (i - 1)) ? 1 : 0);
            }
            if (sum <= k) low = mid;
            else high = mid - 1;
        }
        return low;
    }
}
