public class Solution {
    public long FindMaximumNumber(long k, int x) {
        long left = 1, right = 2*k; // Adjust the upper limit to ensure it's always higher than the possible maximum num
        while (left < right) {
            long mid = left + (right - left) / 2;
            if (SumOfPrices(mid, x) <= k) left = mid + 1;
            else right = mid;
        }
        return left - 1;
    }

    private long SumOfPrices(long num, int x) {
        long sum = 0;
        for (long i = 1; i <= num; i++) {
            sum += CountSetBits(i, x);
        }
        return sum;
    }

    private int CountSetBits(long num, int x) {
        int count = 0;
        for (int pos = 1; num > 0; pos++, num >>= 1) {
            if ((pos % x == 0) && (num & 1) == 1) count++;
        }
        return count;
    }
}
