class Solution {
public:
    long long findMaximumNumber(long long k, int x) {
        long long low = 0, high = 2e18;
        while (low < high) {
            long long mid = low + (high - low + 1) / 2;
            if (check(mid, x) <= k) low = mid;
            else high = mid - 1;
        }
        return low;
    }

private:
    long long check(long long num, int x) {
        long long sum = 0;
        for (long long i = 1; i <= num; i *= 2) {
            // Calculate the contribution of bits set at positions multiple of x
            for (int j = 1; j <= x; j++) {
                if ((i * j) > num) break;
                sum += std::min(num - i * j + 1, i);
            }
        }
        return sum;
    }
};
