class Solution {
public:
    long long findMaximumNumber(long long k, int x) {
        long long left = 1, right = k * x; // Adjust the upper bound based on k and x
        while (left < right) {
            long long mid = left + (right - left) / 2;
            if (calculateSum(mid, x) > k) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        // Check if we've gone too far (if the sum at left is greater than k, subtract one)
        return (calculateSum(left, x) > k) ? left - 1 : left;
    }

private:
    long long calculateSum(long long num, int x) {
        long long sum = 0;
        for (long long i = 1; i <= num; ++i) {
            long long bits = 0;
            for (long long j = i; j > 0; j >>= 1) {
                if (j & 1) ++bits;
            }
            if (i % x == 0) sum += bits; // Only add bits if the position is divisible by x
        }
        return sum;
    }
};
