class Solution {
    public long findMaximumNumber(long k, int x) {
        long low = 1, high = k * x; // Adjust high to ensure the sum of prices does not exceed k
        while (low < high) {
            long mid = (low + high + 1) / 2; // Use upper mid to avoid infinite loop
            if (sumOfPrices(mid, x) <= k) {
                low = mid;
            } else {
                high = mid - 1;
            }
        }
        return low;
    }

    // Calculate sum of prices for numbers from 1 to num
    private long sumOfPrices(long num, int x) {
        long sum = 0, bit = 1;
        for (int i = 1; i <= 64; i++) {
            if (i % x == 0) { // Only consider positions divisible by x
                sum += num / bit;
            }
            bit <<= 1;
        }
        return sum;
    }
}
