class Solution {

    /**
     * @param Integer $k
     * @param Integer $x
     * @return Integer
     */
    function findMaximumNumber($k, $x) {
        $left = 1;
        $right = 2 * $k; // Adjust the upper bound to ensure we don't overshoot.
        while ($left < $right) {
            $mid = $left + ($right - $left) / 2;
            if ($this->price($mid, $x) <= $k) {
                $left = $mid + 1;
            } else {
                $right = $mid;
            }
        }
        return $left - 1;
    }

    private function price($num, $x) {
        $total = 0;
        for ($i = 1; $i <= $num; $i++) {
            // Calculate the contribution of the ith bit if it is set in num's binary representation.
            $bitPosition = pow(2, $i - 1);
            if ($i % $x == 0) { // Check if the position is divisible by x
                $total += intdiv($num, $bitPosition);
            }
        }
        return $total;
    }
}
