impl Solution {
    pub fn find_maximum_number(k: i64, x: i32) -> i64 {
        let mut low = 0;
        let mut high = k;
        while low < high {
            let mid = (low + high + 1) / 2;
            if Solution::calculate_sum(mid, x) <= k {
                low = mid;
            } else {
                high = mid - 1;
            }
        }
        low
    }

    fn calculate_sum(num: i64, x: i32) -> i64 {
        let mut sum = 0;
        let mut multiplier = x as i64;
        while multiplier <= num {
            let full_blocks = num / multiplier;
            let additional_bits = num % multiplier;
            sum += full_blocks * (multiplier / x as i64);
            if additional_bits >= multiplier / 2 {
                sum += additional_bits - multiplier / 2 + 1;
            }
            multiplier *= 2;
        }
        sum
    }
}
