impl Solution {
    pub fn find_maximum_number(k: i64, x: i32) -> i64 {
        let mut left = 0;
        let mut right = 2*k; // Increase upper bound to ensure the solution space is covered
        while left < right {
            let mid = left + (right - left) / 2;
            if Solution::sum_prices(mid, x) > k {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        left - 1
    }

    fn sum_prices(n: i64, x: i32) -> i64 {
        let mut sum = 0;
        for i in 1..=n {
            sum += Solution::bit_count(i) / x as i64 + if Solution::bit_count(i) % x as i64 > 0 { 1 } else { 0 };
        }
        sum
    }

    fn bit_count(mut n: i64) -> i64 {
        let mut count = 0;
        while n > 0 {
            count += n & 1;
            n >>= 1;
        }
        count
    }
}
